const arraySize = Array.from(Array(200).keys())

const result = {
  ...frame,
  fields: frame.fields.map((field) => {
    if (field.name === 'device') {
      return ({
        ...field,
        values: arraySize.map(element => `device${element}`)
      })
    }
    if (field.name === 'title') {
      return ({
        ...field,
        values: arraySize.map(element => `Title -> ${element}`)
      })
    }
    if (field.name === 'value') {
      return ({
        ...field,
        values: arraySize.map(element => Math.floor(Math.random() * element * 3 + 15))
      })
    }
    return ({
      ...field,
      values: arraySize
    })
  })
}

return Promise.resolve(result);

